import React, { useState } from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import Header from "./components/Header";
import Features from "./components/Features";
import Feedback from "./components/Feedback";
import Documentation from "./pages/Documentation";
import Login from "./pages/Login";

const App = () => {
  const [currentPage, setCurrentPage] = useState("home");
  const navigate = useNavigate(); // Для маршрутов

  const navigateTo = (page) => {
    setCurrentPage(page); // Меняем состояние текущей страницы
    navigate(page === "home" ? "/" : `/${page}`); // Программный переход по маршрутам
  };

  return (
    <div>
      {/* Передаём функцию навигации в Header */}
      <Header onNavigate={navigateTo} />

      {/* Отображение главной страницы */}
      {currentPage === "home" && (
        <>
          <Features />
          <Feedback />
        </>
      )}

      {/* Маршруты для остальных страниц */}
      <Routes>
        <Route path="/documentation" element={<Documentation />} />
        <Route path="/login" element={<Login />} />
      </Routes>
    </div>
  );
};

export default App;
