import React, { useState } from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import Header from "./components/Header";
import Features from "./components/Features";
import Feedback from "./components/Feedback";
import Documentation from "./pages/Documentation";
import Login from "./pages/Login";

const App = () => {
  const [currentPage, setCurrentPage] = useState("home");
  const navigate = useNavigate();

  const navigateTo = (page) => {
    setCurrentPage(page);
    navigate(`/${page}`); // Переход по маршруту
  };

  return (
    <div>
      <nav>
        <button onClick={() => navigateTo("home")}>Главная</button>
        <button onClick={() => navigateTo("documentation")}>Документация</button>
        <button onClick={() => navigateTo("login")}>Вход</button>
      </nav>

      {/* Отображение общего компонента Header на главной странице */}
      {currentPage === "home" && (
        <>
          <Header />
          <Features />
          <Feedback />
        </>
      )}

      {/* Маршруты для страниц "Документация" и "Вход" */}
      <Routes>
        <Route path="/documentation" element={<Documentation />} />
        <Route path="/login" element={<Login />} />
      </Routes>
    </div>
  );
};

export default App;
