import React, { useState } from "react";
import { Routes, Route, useNavigate, useLocation } from "react-router-dom";
import Header from "./components/Header";
import Features from "./components/Features";
import Feedback from "./components/Feedback";
import Documentation from "./pages/Documentation";
import Login from "./pages/Login";

const App = () => {
  const [currentPage, setCurrentPage] = useState("home");
  const navigate = useNavigate();
  const location = useLocation(); // Используем для определения текущего маршрута

  const navigateTo = (page) => {
    setCurrentPage(page);
    navigate(`/${page}`); // Выполняем навигацию
  };

  // Определяем, показывать ли Header (только на главной странице)
  const showHeader = location.pathname === "/" || currentPage === "home";

  return (
    <div>
      {/* Header отображается только на главной странице */}
      {showHeader && <Header currentPage={currentPage} onNavigate={navigateTo} />}

      {/* Главная страница */}
      {currentPage === "home" && location.pathname === "/" && (
        <>
          <Features />
          <Feedback />
        </>
      )}

      {/* Маршруты для страниц */}
      <Routes>
        <Route path="/" element={<Features />} /> {/* Главная страница */}
        <Route path="/documentation" element={<Documentation />} />
        <Route path="/login" element={<Login />} />
      </Routes>
    </div>
  );
};

export default App;
